INSERT INTO `wp_eventscalendar_main` (`id`, `eventTitle`, `eventDescription`, `eventLocation`, `eventLinkout`, `eventStartDate`, `eventStartTime`, `eventEndDate`, `eventEndTime`, `accessLevel`, `postID`) VALUES
(1, 'Mesa redonda', 'Irá ococorrer.......', 'ufrn', NULL, '0000-00-00', '24:00:00', '0000-00-00', '01:00:00', 'public', 791),
(3, 'Passeata', ',shgfjkhsdg jkhgfjksdgh jfg sdjg jhs df', 'Rua', NULL, '0000-00-00', '13:00:00', '0000-00-00', '15:00:00', 'public', 793),
(4, 'teste', 'ksnd,lgmnsd,m ', 'rua', NULL, '0000-00-00', '00:13:00', '0000-00-00', '00:15:00', 'public', 794),
(29, 'Mesa Redonda sobre desarmamento na UFRN', 'Autoridades locais e professores da Universidade Federal do Rio Grande do Norte (UFRN) debaterão o desarmamento em uma mesa-redonda. Para obtenção de certificados, os interessados deverão fazer as inscrições por meio do SIGAA.\r\n\r\nO evento, que é direcionado a professores, técnicos, estudantes da Instituição e sociedade potiguar, é promovido pela Pró-reitoria de Extensão (PROEX).\r\n\r\nLOCAL: Auditório da Biblioteca Central Zila Mamede da UFRN – Campus Central, Natal/RN.', NULL, NULL, '2011-06-17', '19:00:00', '2011-06-17', '21:30:00', 'public', 961),
(33, 'Evento \\"SESI e você contra a Dengue\\": ', 'A proposta do SESI DR/RN é realizar uma ação, denominada \\"SESI e você contra a Dengue\\", com o objetivo de sensibilizar a população do nosso estado para o combate à Dengue.\r\n', 'Ponta Negra- Vila e Conjunto Bairro de Mãe Luiza, bairro de Bom Pastor, bairro de Felipe Camarão, Estação Rodoviária de Natal, Estação Ferroviária da Ribeira e no \\"Trevo de Igapó\\"', NULL, '2011-06-11', '08:00:00', '2011-06-11', '12:00:00', 'public', 1158),
(30, 'Exposição Itinerante e Interativa na BCZM', 'Exposição \\"Nem tudo que é Sólido Desmancha no Ar\\" na BCZM', NULL, NULL, '2011-06-06', NULL, '2011-06-10', NULL, 'public', 972),
(32, 'Início da Exposição 2011 do Pólo Arte na Escola- DEART/ UFRN', 'Exposição Coletiva de Professores e Alunos da UFRN e artistas convidados.\r\n\r\nPeríodo: de 4 a 26 de agosto de 2011\r\nLocal: Galeria Conviv\\''art/ UFRN\r\n\r\nInscrições: 16 de maio à 1 de julho de 2011\r\nTelefone: 3215-3553\r\nEdital em: http://artenaescolarn.blogspot.com/', NULL, NULL, '2011-08-04', NULL, '2011-08-04', NULL, 'public', 988),
(34, 'Congresso de Educação em Natal', 'Já estão abertas as inscrições para o Congresso de Educação de Natal. O evento trará entre os palestrantes, nomes como, Eduardo Shinyashiki (professor e escritor), Augusto Cury (médico e escritor) e Hamilton Werneck (pedagogo).\r\n\r\nO evento pretende reunir profissionais da educação, pais e estudantes da área com o objetivo de compartilhar experiências na área de ensino, contribuindo para a formação continuada de professores.\r\n\r\nLocal: Hotel Praia Mar\r\n\r\nPeríodo: 15 e 16 de Julho de 2011\r\n\r\nInscrições: Os interessados em participar, devem se inscrever até o dia 25/06, através do sitewww.congressojuazeiro.com.br.\r\n\r\nOutras informações podem ser adquiridas através do 0800 702 7585.', NULL, 'http://www.congressojuazeiro.com.br', '2011-07-15', NULL, '2011-07-15', NULL, 'public', 1159),
(35, 'Exposição Itinerante e Interativa no RU', 'Exposição "<i><font color=red>Nem tudo que é sólido desmancha no ar</font></i>" no RU', NULL, NULL, '2011-06-13', NULL, '2011-06-16', NULL, 'public', 1160);
