:5:{s:4:"data";s:31:"Tue, 07 Jun 2011 17:43:33 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:14:"blazestreaming";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"Weblog Tools Collection: New WordPress Default Theme Unveiled";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10114";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/jdR2fJqdkuI/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1333:"<p><a href="http://theme.wordpress.com/themes/twentyeleven/">Twenty Eleven</a>, the new <a href="http://wordpress.org/">WordPress</a> default theme, has been <a href="http://en.blog.wordpress.com/2011/06/06/new-theme-twenty-eleven/">officially unveiled</a>. If you notice the similarities between Twenty Eleven and <a href="http://wordpress.org/extend/themes/duster">Duster</a>, that&#8217;s because Duster was more or less the beta release of Twenty Eleven, but don&#8217;t be fooled by the similar looks. Twenty Eleven contains several under-the-hood enhancements and additional features, and it will be included by default with all WordPress downloads as soon as WordPress 3.2 is released!</p>\n<p><a href="http://wordpress.com/">WordPress.com</a> users are welcome to try Twenty Eleven on their blogs now, and self-hosted WordPress users eager to try Twenty Eleven are more than welcome to <a href="http://wordpress.org/extend/plugins/wordpress-beta-tester/">get involved</a> in WordPress 3.2 beta testing. Of course, if beta testing isn&#8217;t your thing, you can always try out <a href="http://twentyelevendemo.wordpress.com/">the theme&#8217;s demo</a>.</p>\n<p>What do you think of the new default WordPress theme?</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/jdR2fJqdkuI" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Jun 2011 13:00:45 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:10;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"Weblog Tools Collection: WordPress Theme Releases for 6/6";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10109";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/YQv679uLSPw/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1149:"<p><img class="alignnone size-full wp-image-10110" title="char" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/char.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://emptynestthemes.emptynestheritage.com/?p=1376"><strong>Char</strong></a> is a dark, edgy, and grungy theme.</p>\n<p><img class="alignnone size-full wp-image-10111" title="gridnotez" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/gridnotez.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.simplywp.net/2011/06/02/gridnotez-theme/"><strong>Gridnotez</strong></a> is a two column theme with a brown grid-paper design and hand-drawn icons.</p>\n<p><img class="alignnone size-full wp-image-10112" title="scyllalite" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/scyllalite.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://wordpress.org/extend/themes/scylla-lite"><strong>Scylla lite</strong></a> has awesome skins, fonts, slider, layouts, and an easy to use theme options panel.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/YQv679uLSPw" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 06 Jun 2011 13:00:16 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:11;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:58:"Weblog Tools Collection: WordPress Plugin Releases for 6/4";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10104";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/r5-Sl89qk5w/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1283:"<h3>New plugins</h3>\n<p><a href="http://www.ahsan.pk/2011/06/google-1button/"><strong>+1 Button Plugin</strong></a> adds a Google +1 button to your posts.</p>\n<p><a href="http://wordpress.org/extend/plugins/google/"><strong>The Google +1 plugin</strong></a> adds the Google +1 button to your site so your visitors can vote to tell the world how great your site is.</p>\n<p><a href="http://wordpress.org/extend/plugins/hide-unwanted-shortcodes/"><strong>Hide Unwanted Shortcodes</strong></a> prevents unwanted shortcodes from showing on blog.</p>\n<p><a href="http://wordpress.org/extend/plugins/update-active-plugins-only/"><strong>Update Active Plugins Only</strong></a> tells WordPress to only check for updates to active plugins.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://scribu.net/wordpress/front-end-editor"><strong>Front-end Editor</strong></a> allows inline editing of your content.</p>\n<p><a href="http://wordpress.org/extend/plugins/theme-check/"><strong>Theme-Check</strong></a> allows you to run checks on the current theme before uploading to WordPress. This plugin uses the same API and is kept more or less in sync with the WordPress.org uploader tool.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/r5-Sl89qk5w" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 04 Jun 2011 13:00:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:12;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:72:"Lorelle on WP: Managing Multiple Authors: Customizing the Author Bio Box";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:36:"http://lorelle.wordpress.com/?p=5115";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:97:"http://lorelle.wordpress.com/2011/06/03/managing-multiple-authors-customizing-the-author-bio-box/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:500:"In this series on managing multiple bloggers in WordPress, I&#8217;ve covered how to showcase the authors within the WordPress Theme design, talked about how to manage author content within WordPress to help you see all the ways you can promote your authors within the design and content management system, and started showing you code and [...]<img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=lorelle.wordpress.com&blog=72&post=5115&subd=lorelle&ref=&feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 04 Jun 2011 01:38:47 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Lorelle VanFossen";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:13;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:30:"Matt: Seattle WP Meetup Friday";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38292";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:46:"http://ma.tt/2011/06/seattle-wp-meetup-friday/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:974:"<p><a href="http://ma.tt/2005/04/seattle-meetup/">Six years ago on this blog we scheduled a WordPress meetup in Seattle</a> which ended up including a number of folks who are still changing the web today, including <a href="http://www.brepettis.com/">Bre Pettis</a>, <a href="http://scobleizer.com/">Robert Scoble</a>, <a href="http://chris.pirillo.com/">Chris Pirillo</a>, <a href="http://www.bestkungfu.com/">Matt May</a>, <a href="http://www.fortes.com/">Filipe Fortes</a>, <a href="http://skeltoac.com/">Andy Skelton</a>, <a href="http://www.scottberkun.com/">Scott Berkun</a>, and <a href="http://www.commoncraft.com/">Lee Lefever</a>. We&#8217;re going to do an informal 2.0 <strong>tonight at 6 PM, Friday June 3 at Pike Pub &amp; Brewery</strong> on 1st Avenue in downtown Seattle. Come by and share a beer, reminiscence about trackbacks, and talk about the future of the open web. It&#8217;s short notice, so please spread the word to your Seattle-area friends.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 03 Jun 2011 18:38:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:14;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"Weblog Tools Collection: WordPress Theme Releases for 6/2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10095";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/aRgT2rDh9uA/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1142:"<p><img class="alignnone size-full wp-image-10096" title="brunelleschi" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/brunelleschi.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://wordpress.org/extend/themes/brunelleschi"><strong>Brunelleschi</strong></a> is a clean and modern theme that celebrates content.</p>\n<p><img class="alignnone size-full wp-image-10097" title="driftwood" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/driftwood.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://emptynestthemes.emptynestheritage.com/?p=1358"><strong>Driftwood</strong></a> is a perfect theme for the onset of summer.</p>\n<p><img class="alignnone size-full wp-image-10098" title="mantra" src="http://weblogtoolscollection.com/wp-content/uploads/2011/06/mantra.jpg" alt="" width="150" height="97" /></p>\n<p><a href="http://wordpress.org/extend/themes/mantra"><strong>Mantra</strong></a> is a clean and very web 2.0 theme with customizable layout, colors, fonts and graphics.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/aRgT2rDh9uA" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 02 Jun 2011 13:00:57 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:15;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"Weblog Tools Collection: WordCamp SF 2011 Call for Speakers";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10092";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/pqlpsjtnE1M/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:842:"<p><a href="http://2011.sf.wordcamp.org/">WordCamp San Francisco</a>, &#8220;the official annual conference of the <a href="http://wordpress.org/">WordPress</a> open source project&#8221; and heralded by many as the largest annual gathering of WordPressians, <a href="http://2011.sf.wordcamp.org/2011/05/31/call-for-speakers/">is putting out the call for speakers</a> for the 2011 convention.</p>\n<p>This year&#8217;s WordCamp SF will be held at the <a href="http://www.acc-missionbayconferencecenter.com/">Mission Bay Conference Center</a> from August 12th &#8211; 14th, and they are &#8220;expanding the programming for publishers, bloggers, and developers.&#8221;</p>\n<p>I&#8217;ll definitely be there this year. How about you?</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/pqlpsjtnE1M" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 01 Jun 2011 13:00:44 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:16;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"Alex King: WordPress Code Snippet to Detect “Main” Loop";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:27:"http://alexking.org/?p=5879";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:78:"http://alexking.org/blog/2011/06/01/wordpress-code-snippet-to-detect-main-loop";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:929:"<p>Cribbed from the <a href="http://lists.automattic.com/pipermail/wp-hackers/2011-May/039467.html" rel="external">WP-Hackers list</a>, the following sample code should give you a good way to test if the current &#8220;in the loop&#8221; action is within the main loop or a different loop:</p>\n<div class="code">\n<pre><code>function main_loop_test($query) {\n  global $wp_the_query;\n  if ($query === $wp_the_query) {\n    echo "main loop";\n  }\n}\nadd_action(''loop_start'', ''main_loop_test'');</code></pre>\n</div>\n<p>Thanks <a href="http://andrewnacin.com" rel="external">Nacin</a> and Konrad.</p>\n<p>This is useful if you&#8217;re creating a plugin that needs to be compatible with multiple themes, otherwise for a theme implementation there are better ways to check for this (for example, setting/unsetting a bit around the main loop call).</p>\n<img src="http://alexking.org/wp/?ak_action=api_record_view&id=5879&type=feed" alt="" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 01 Jun 2011 08:10:45 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Alex";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:17;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:50:"Dev Blog: WordCamp San Francisco Call for Speakers";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:33:"http://wordpress.org/news/?p=1886";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:75:"http://wordpress.org/news/2011/05/wordcamp-san-francisco-call-for-speakers/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:904:"<p>The annual WordPress conference, <a href="http://2011.sf.wordcamp.org/">WordCamp San Francisco</a> (home of the very first WordCamp), is now accepting speaker applications. Past speakers have included core WordPress developers, people building successful businesses on WordPress, popular bloggers, people from related projects and businesses&#8230;you name it. In addition to Matt Mullenweg&#8217;s annual &#8220;State of the Word&#8221; address, WCSF has played host to talks by people like Mark Jaquith, Matt Cutts, Richard Stallman, Scott Berkun, Karl Fogel, Tim Ferriss, Tara Hunt, Chris Pirillo, and John Lilly. With 3 days of content this year instead of just one, the list of speakers should be even more impressive. If you think you&#8217;d make a good addition to this year&#8217;s roster, check out the <a href="http://2011.sf.wordcamp.org/call-for-speakers/">WCSF Call for Speakers</a>.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 31 May 2011 18:59:48 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"Jane Wells";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:18;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:64:"Publisher Blog: Primer: WordPress photo gallery without a plugin";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:43:"http://publisherblog.automattic.com/?p=1515";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:95:"http://publisherblog.automattic.com/2011/05/31/primer-wordpress-photo-gallery-without-a-plugin/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2605:"<p><a href="http://ottopress.com/">Otto</a> has created a very thorough <a href="http://ottopress.com/2011/photo-gallery-primer/">primer to create a WordPress photo gallery without a plugin</a>. He walks you through understanding pictures and galleries, creating and modifying an image template, and tweaking image and thumbnail sizes.</p>\n<p>He reminds us:</p>\n<blockquote><p>You don’t really need plugins to create photo galleries. WordPress has a huge amount of gallery functionality built right in. You just need to make your theme smarter in order to take advantage of it.</p></blockquote>\n<p>Click through to read <a href="http://ottopress.com/2011/photo-gallery-primer/">Photo gallery primer on Otto on WordPress</a>.</p>\n<p><em></em><strong><em><a href="http://get.wp.com/"><strong>Want WordPress for your site? Get.WordPress.com</strong></a></em></strong></p>\n<br />  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godelicious/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/delicious/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gofacebook/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/facebook/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gotwitter/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/twitter/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gostumble/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/stumble/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godigg/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/digg/wordpresspublishers.wordpress.com/1515/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/goreddit/wordpresspublishers.wordpress.com/1515/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/reddit/wordpresspublishers.wordpress.com/1515/" /></a> <img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=publisherblog.automattic.com&blog=1470857&post=1515&subd=wordpresspublishers&ref=&feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 31 May 2011 16:30:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"Sara Rosso";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:19;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:42:"Joseph: pressfs – A WordPress Filesystem";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://josephscott.org/?p=4204";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:71:"http://josephscott.org/archives/2011/05/pressfs-a-wordpress-filesystem/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:7220:"<p>Here is something else I&#8217;ve been toying with: <a href="https://github.com/josephscott/pressfs">pressfs</a>, a WordPress filesystem.  Currently it exposes user, post, tag, and category data in a read-only filesystem.</p>\n<h3>The Short Version</h3>\n<p>This is a <a href="http://www.python.org/">Python</a> script that uses <a href="http://fuse.sourceforge.net/">FUSE</a> to expose data from a <a href="http://wordpress.org/">WordPress</a> site as a filesystem.</p>\n<p>For the impatient you can give this a try in just a few steps:</p>\n<ul>\n<li>get the <a href="https://github.com/josephscott/pressfs">pressfs source code</a> from Github</li>\n<li>install and activate the pressfs WordPress plugin</li>\n<li>copy example-config.ini config.ini</li>\n<li>edit config.ini, set values in WordPress section</li>\n<li>python pressfs.py /your/mount/point/</li>\n</ul>\n<p>This code is still roughly beta quality, try it out on a test/dev WordPress install first.  Authentication is just HTTP basic, so please use it over SSL.  It also needs to use an administrator level WordPress account (something that might change in the future).</p>\n<h3>History</h3>\n<p>A little over two years ago I started thinking about writing a WordPress plugin to act as a WebDAV server so that we could expose media uploads as an easily mounted filesystem.  I poked around a bit, but quickly put the idea on the shelf and worked on other things.  Fast forward to early 2011, after I got <a href="http://josephscott.org/archives/2011/05/an-irc-experiment-raifbot/">raifbot</a> into a functional state I started taking a more serious look at WordPress + WebDAV.  As a concept this is definitely plausible, but after trying several different approaches I decided I wasn&#8217;t interested in dealing with the edge cases I ran into (<a href="http://nginx.org/">nginx</a> not having built in support for <a href="http://en.wikipedia.org/wiki/Chunked_transfer_encoding">chunked encoding</a> was particularly irksome).</p>\n<p>Although I wasn&#8217;t happy with the WebDAV issues, I still kept thinking about ways we could expose WordPress resources as a filesystem.  A few weeks later I started experimenting with the idea of using FUSE to expose WordPress data as a filesystem.  It was a rough start, but I finally got it to a point where it was time to share with others and get more feedback.</p>\n<h3>Python</h3>\n<p>I don&#8217;t have much experience writing Python code, which isn&#8217;t surprising since my full time job has involved WordPress and WordPress related development (lots of PHP code).  Sure, I&#8217;ve tweaked a few Python scripts from time to time, read <a href="http://diveintopython.org/">Dive Into Python</a> and toyed with some of the examples, but I&#8217;ve never sat down to write new code for a new project in Python until now. I welcome feedback on improving the code, just remember this is my first time sailing this ship, so be gentle :-)</p>\n<p>The requirements for running pressfs aren&#8217;t terribly exotic.  Obvious you need to have the FUSE library installed and the <a href="http://sourceforge.net/projects/fuse/files/fuse-python/">fuse-python</a> bindings.  Other external libraries it uses include httplib2 and simplejson.  I think all of the other imported code are standard for most python installs.</p>\n<p>There are only a handful of examples and tutorials on getting started with fuse-python, which I had to experiment with repeatedly (along with tons of debugging) to figure out how the pieces fit together.  I&#8217;ll likely be posting tutorials and how-to&#8217;s about python-fuse, both to help others and to make sure I have a clear idea of how the various features work.</p>\n<h3>The WordPress Plugin</h3>\n<p>I choose to write a new WordPress plugin to expose the data I needed because I wanted to be able to tweak it on a whim.  Using JSON to exchange data was a nice bonus as well.  So for now at least you&#8217;ll need to have this active on any site that you want to use pressfs with.</p>\n<h3>Directory Layout</h3>\n<p>If you have pressfs mounted on <code>/var/wordpress</code> you&#8217;ll see something like this:</p>\n<ul>\n<li>/var/wordress/categories/</li>\n<li>/var/wordpress/posts/</li>\n<li>/var/wordpress/tags/</li>\n<li>/var/wordpress/users/</li>\n</ul>\n<p>The categories directory will list all of the categories, using the category slug.  Each individual category is a directory with the following files in it: count, description, id, name, parent, and slug.  These all contain what you would expect.</p>\n<p>The posts directory lists the posts on your site, with the format of &lt;POSTID&gt;-&lt;NAME&gt; (or if NAME isn&#8217;t available &lt;POSTID&gt;-&lt;TITLE&gt;).  Each post directory has the following files in it: content, date-gmt, id, name, password, status, title, type, and url.  Shouldn&#8217;t be any surprises about the contents of each of those files.</p>\n<p>The tags directory lists the tags on your site, using the slug.  Each tag directory contains: count, description, id, name, and slug.</p>\n<p>The users directory lists all the users on your site, with directory entries using the username field.  Each user directory contains these files: display-name, email, id, login, nice-name, registered, and url.</p>\n<p>Want to see the description field for category photos:<br />\n<code>more /var/wordress/categories/photos/description</code></p>\n<p>Want to see the post body for the post id 123, which has the name summer-vacation:<br />\n<code>more /var/wordpress/posts/123-summer-vacation/content</code></p>\n<p>Want the email field from user josephscott:<br />\n<code>more /var/wordpress/users/josephscott/email</code></p>\n<h3>The Future</h3>\n<p>The largest limitation that I expect people to ask about is moving from read-only to allowing write operations.  The good news is that I already have experimental code that allows for writes of specific fields.  I have more testing and cleaning up of the code to do, but I intended to add write support in a future version.</p>\n<p>Next up is exposing more WordPress data.  I already have test code for exposing media upload files, so that will get added eventually.  Beyond that I&#8217;d like to expose other less obvious items as well, like settings.</p>\n<p>While I&#8217;ve revised this a few times before getting to this point, I&#8217;m not sure I&#8217;ve got everything just right.  For instance, is the current directory layout really the best way to go?  Don&#8217;t be surprised if there are significant changes as development goes along.</p>\n<h3>Conclusion</h3>\n<p>Now that I&#8217;ve gotten my feet wet with WordPress  + python-fuse I&#8217;m excited about doing more with this.  This was also a good way to start learning more Python, for something that resembles a real application, which I&#8217;ve wanted to do for some time now.</p>\n<p>In the short term I&#8217;d like to hear how this works (or doesn&#8217;t) for others.  If you run into a problem re-mount pressfs with <code>python pressfs.py /your/mount/point/ -d</code>, which will keep the process running in the foreground, with details on what each operation does (including errors).  That should provide enough details to figure out where the problem is.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 31 May 2011 14:46:38 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Joseph Scott";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:20;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"Weblog Tools Collection: WordPress Plugin Releases for 5/31";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10088";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/njzpPDD5-zk/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1577:"<h3>New plugins</h3>\n<p><a href="http://noscope.com/journal/2011/05/cleanup-html-wordpress-plugin"><strong>Cleanup HTML</strong></a> adds a button to your visual editor, which when clicked, strips your post HTML of any div or span tags you might have, usually (but not always) junk tags to have in a post.</p>\n<p><a href="http://wordpress.org/extend/plugins/easy-modal/"><strong>Easy Modal</strong></a> allows you to easily add a Modal window with just about any content.</p>\n<p><a href="http://wordpress.org/extend/plugins/revert/"><strong>Revert</strong></a> is a one-click button to revert a post to its previous revision.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://wordpress.org/extend/plugins/admin-menu-tree-page-view/"><strong>Admin Menu Tree Page View</strong></a> is a WordPress plugin that adds a page tree in the admin menu, so all your pages will always be available within just one click, no matter where you are in the admin area.</p>\n<p><a href="http://wordpress.org/extend/plugins/awesome-flickr-gallery-plugin/"><strong>Awesome Flickr Gallery Plugin</strong></a> is a simple, fast and light plugin to create a gallery of your Flickr photos on your WordPress enabled website.</p>\n<p><a href="http://ocaoimh.ie/exploit-scanner/"><strong>Exploit Scanner</strong></a> searches the files on your website, and the posts and comments tables of your database for anything suspicious. It also examines your list of active plugins for unusual filenames.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/njzpPDD5-zk" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 31 May 2011 13:00:29 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:21;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:35:"Matt: Benoît Mandelbrot and Chance";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38289";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:50:"http://ma.tt/2011/05/benoit-mandelbrot-and-chance/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:881:"<div class="blockquote"><blockquote><p>Louis Pasteur is credited with the observation that chance can only help the well-prepared mind. I also think that my long string of lucky breaks can be credited to my mode of paying attention: I look at funny things and never hesitate to ask questions. Most people would not have noticed the dirty blackboard, or looked at the article that my uncle gave me because he was not interested.</p></blockquote></div>\n<p>via <a href="http://www.edge.org/3rd_culture/obrist10/obrist10_index.html">Edge: THE FATHER OF LONG TAILS — Interview with Benoît Mandelbrot by Hans Ulrich Obrist</a>. For extra credit and an exercise in brevity and clarity, link or write <a href="http://ma.tt/2011/05/benoit-mandelbrot-and-chance/">in the comments</a> the simplest definition of &#8220;fractal&#8221; as used by Mandelbrot and Obrist in the interview.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 30 May 2011 16:23:58 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:22;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:64:"Weblog Tools Collection: Use any WordPress Theme with BuddyPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10083";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/hJ45Rrxy38E/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:922:"<p>The <a href="http://wordpress.org/extend/plugins/bp-template-pack/">BuddyPress Template Pack</a> allows you to use any <a href="http://wordpress.org/">WordPress</a> theme with <a href="http://buddypress.org/">BuddyPress</a>, and it&#8217;s the perfect solutions for businesses and bloggers who are seeking to upgrade to more of a BuddyPress platform without changing too much of their existing design.</p>\n<p>Previous users of the BuddyPress Template Pack will be happy to know that the plugin <a href="http://buddypress.org/2011/05/buddypress-template-pack-1-1/">has been upgraded</a> and is now fully compatible with the latest stable release of BuddyPress.</p>\n<p>Have you used the BuddyPress Template Pack? Was exactly what you had hoped it to be, or would you recommend any similar plugins or services?</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/hJ45Rrxy38E" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Mon, 30 May 2011 13:00:42 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:23;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:27:"Matt: Liking is for Cowards";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38284";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:43:"http://ma.tt/2011/05/liking-is-for-cowards/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:657:"<div class="blockquote"><blockquote><p>I may be overstating the case, a little bit. Very probably, you’re sick to death of hearing social media disrespected by cranky 51-year-olds. My aim here is mainly to set up a contrast between the narcissistic tendencies of technology and the problem of actual love. My friend Alice Sebold likes to talk about “getting down in the pit and loving somebody.” She has in mind the dirt that love inevitably splatters on the mirror of our self-regard.</p></blockquote></div>\n<p>Johnathan Franzen&#8217;s <a href="http://www.nytimes.com/2011/05/29/opinion/29franzen.html">Liking is for Cowards in the NY Times</a>.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sun, 29 May 2011 19:08:52 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:24;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:58:"Weblog Tools Collection: WordPress Theme Releases for 5/29";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10077";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/RWp2qEAnj2E/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1256:"<p><img class="alignnone size-full wp-image-10078" title="emphasis" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/emphasis.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://emptynestthemes.emptynestheritage.com/?p=1349"><strong>Emphasis</strong></a> is a black and blue two-column theme.</p>\n<p><img class="alignnone size-full wp-image-10079" title="lefty" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/lefty.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://www.wpexplorer.com/lefty-wordpress-theme.html"><strong>Lefty</strong></a> is a left aligned premium like free WordPress theme that features a very modern and clean design perfect for all sorts of blogs.</p>\n<p><img class="alignnone size-full wp-image-10080" title="zenlitevertical" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/zenlitevertical.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://quirm.net/zenlite/zenlite-vertical/"><strong>ZenLite Vertical</strong></a> is a <a href="http://quirm.net/zenlite/">ZenLite</a> child theme for those who who want a clean, light, two-column site.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/RWp2qEAnj2E" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sun, 29 May 2011 13:00:51 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:25;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:52:"Weblog Tools Collection: bbPress 2.0 Beta 2 Released";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10071";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/QQ1nMqsFsmk/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1306:"<p><a href="http://bbpress.org/">bbPress</a> 2.0 Beta 2 <a href="http://bbpress.org/blog/2011/05/bbpress-2-0-beta-2/">has been released</a>. bbPress development is moving much quicker than anticipated due mostly to volunteer contributions. Since <a href="http://weblogtoolscollection.com/archives/2011/05/22/bbpress-2-beta-1-released/">the previous release</a>, the following changes have been made:</p>\n<ul>\n<li><a href="http://blog.glotpress.org/">GlotPress</a> integration</li>\n<li>Fixes Forum archive bug on some installations</li>\n<li>Fixes and improvements to importer</li>\n<li>Adds home link support to breadcrumb</li>\n<li>Adds missing breadcrumbs to various template files</li>\n<li>Adds filters for future anti-spam support</li>\n<li>Improvements to templates and CSS</li>\n<li>Improvements to Theme Compatibility</li>\n<li>Improved RTL support</li>\n<li>Improved multisite support</li>\n<li>Topic/reply trash fixes</li>\n</ul>\n<p>bbPress 2 is still not necessarily ready for use on a live site yet, but bbPress theme and plugin developers are encouraged to start testing now. If you encounter any bugs, please report them to the <a href="http://bbpress.trac.wordpress.org/">bbPress Trac</a>.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/QQ1nMqsFsmk" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 28 May 2011 13:00:31 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:26;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:23:"Joseph: PuSHPress 0.1.7";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:30:"http://josephscott.org/?p=4187";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"http://josephscott.org/archives/2011/05/pushpress-0-1-7/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:788:"<p><a href="http://code.google.com/p/pubsubhubbub/"><img alt="" src="http://code.google.com/apis/pubsubhubbub/images/hubbub.png" title="PubSubHubbub" class="alignleft" width="113" height="38" /></a></p>\n<p>Version 0.1.7 of the <a href="http://wordpress.org/extend/plugins/pushpress/">PuSHPress WordPress plugin</a> is now live.  This includes fixes to various edge cases that have come up over time.  Details are available in the <a href="http://wordpress.org/extend/plugins/pushpress/changelog/">changelog</a>.</p>\n<p>Thank you to <a href="http://urbangiraffe.com/">John Godley</a>, <a href="http://andrewnacin.com/">Andrew Nacin</a>, Hugo Hallqvist (couldn&#8217;t find a link for him), and <a href="http://blogwaffe.com/">Mike Adams</a> for reporting issues and submitting patches.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 May 2011 19:15:22 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Joseph Scott";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:27;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:41:"Mike Little: WordPress’ Eighth Birthday";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:35:"http://zed1.com/journalized/?p=1813";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:143:"http://zed1.com/journalized/archives/2011/05/27/wordpress-eighth-birthday/?utm_source=rss&utm_medium=rss&utm_campaign=wordpress-eighth-birthday";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2646:"<p>Today is WordPress’ official eighth birthday (the anniversary of the <a href="http://wordpress.org/news/2003/05/wordpress-now-available/">first release</a>).</p>\n<p>I still marvel at the incredible distance it has come. I’m also still proud that I had a part in its birth. But even more, I marvel at the wonderful contribution of all the WordPress community make to this fantastic project.</p>\n<p>A client said to me this morning “This WordPress is brilliant isn’t it?” As I helped him set up his fourth WordPress site. You can’t get much clearer praise than that.</p>\n<p>So raise a virtual beer (or other non-alcoholic beverage if, like me, you are teetotal) to WordPress, the community, and to another year.</p>\n<p><strong>Update:</strong> I just spotted this tweet from <a href="http://twitter.com/#!/nacin">Andrew Nacin</a>:</p>\n<!-- tweet id : 74139775761793024 -->#bbpBox_74139775761793024 a { text-decoration:none; color:#277dac; }#bbpBox_74139775761793024 a:hover { text-decoration:underline; }<div id="bbpBox_74139775761793024" class="bbpBox"><div><span>At more than 20 million WordPress.com blogs, that puts WordPress at north of 45 million sites. Wowza. Happy birthday indeed.</span><div class="bbp-actions"><img align="middle" src="http://zed1.com/journalized/wp-content/plugins/twitter-blackbird-pie//images/bird.png" /><a title="tweeted on Friday, 27 May 2011 16:47" href="http://twitter.com/#!/nacin/status/74139775761793024" target="_blank">Friday, 27 May 2011 16:47</a> via web<a href="https://twitter.com/intent/tweet?in_reply_to=74139775761793024" class="bbp-action bbp-reply-action" title="Reply"><span><em></em><strong>Reply</strong></span></a><a href="https://twitter.com/intent/retweet?tweet_id=74139775761793024" class="bbp-action bbp-retweet-action" title="Retweet"><span><em></em><strong>Retweet</strong></span></a><a href="https://twitter.com/intent/favorite?tweet_id=74139775761793024" class="bbp-action bbp-favorite-action" title="Favorite"><span><em></em><strong>Favorite</strong></span></a></div><div><a href="http://twitter.com/intent/user?screen_name=nacin"><img src="http://a0.twimg.com/profile_images/1109017906/twitter-square-large_normal.png" /></a></div><div><a href="http://twitter.com/intent/user?screen_name=nacin">@nacin</a><div>Andrew Nacin</div></div><div></div></div></div><!-- end of tweet -->\n<p>Wow! 25 million <a href="http://wordpress.org/">standalone WordPress</a> sites plus 2o million <a href="http://WordPress.com">WordPress.com</a> sites! No wonder it <a href="http://w3techs.com/technologies/overview/content_management/all">powers more than 14 percent of the web</a>.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 May 2011 15:29:05 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"mike";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:28;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:59:"Weblog Tools Collection: WordPress Plugin Releases for 5/27";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10067";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/xLIbxe-K8nM/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1252:"<h3>New plugins</h3>\n<p><a href="http://www.ahsan.pk/2011/05/wp-plugin-adsense-float/"><strong>Adsense Float</strong></a> shows Google AdSense ads at a prominent position on your site, which maximizes the CTR and revenue of your site.</p>\n<h3>Updated plugins</h3>\n<p><a href="http://wordpress.org/extend/plugins/catablog/"><strong>CataBlog</strong></a> is a comprehensive and effortless tool that allows you to create catalogs, stores and galleries for your blog.</p>\n<p><a href="http://sudarmuthu.com/wordpress/easy-retweet"><strong>Easy Retweet</strong></a> let’s you add retweet or Tweet this buttons for your WordPress posts, together with the retweet count.</p>\n<p><a href="http://wordpress.org/extend/plugins/hotfix/"><strong>Hotfix</strong></a> provides unofficial fixes for selected WordPress bugs, so you don&#8217;t have to wait for the next WordPress core release.</p>\n<p><a href="http://www.designchemical.com/blog/index.php/wordpress-plugins/wordpress-plugin-jquery-drill-down-ipod-menu-widget/"><strong>jQuery Drill Down iPod Menu</strong></a> creates drill down iPod style menus from any WordPress custom menu using jQuery.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/xLIbxe-K8nM" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 May 2011 13:00:44 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:29;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:28:"bbPress: bbPress 2.0, Beta 2";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:25:"http://bbpress.org/?p=422";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:51:"http://bbpress.org/blog/2011/05/bbpress-2-0-beta-2/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2021:"<p>Surprise! The development of bbPress 2.0 is progressing faster than expected! We released Beta 1 only six days ago, and today we’re releasing <a href="http://wordpress.org/extend/plugins/bbpress/">Beta 2</a>. Huge props go out to everyone that&#8217;s already test driving Beta 1. The rapid pre-release cycle is due entirely to you, and sour cream and onion potato chips. <img src="http://bbpress.org/wordpress/wp-includes/images/smilies/icon_smile.gif" alt=":)" class="wp-smiley" /> </p>\n<p>Remember that this is still very much beta software, so don’t go using it on your production sites. If you’re a plugin or theme developer, or a site administrator, you should be running this on test environments and reporting any bugs you find. If you’re a loyal bbPress user who is anxious to see what the future of bbPress looks like, create a secondary WordPress test site and let us know what you think about it in the support forums.</p>\n<p>The plan is to keep releasing Beta&#8217;s as changes go in, put out release candidates in the next few weeks, and release bbPress 2.0 by the end of June (in tandem with <a href="http://wordpress.org/news/2011/05/wordpress-3-1-3/">WordPress 3.2</a>.) The more you help us polish the new paint during the beta period means a shinier and longer lasting 2.0, and the more likely we are to stay on schedule and hit our release goals.</p>\n<p>Here are some of the important changes since Beta 1:</p>\n<ul>\n<li>GlotPress integration</li>\n<li>Fixes Forum archive bug on some installations</li>\n<li>Fixes and improvements to importer</li>\n<li>Adds home link support to breadcrumb</li>\n<li>Adds missing breadcrumbs to various template files</li>\n<li>Adds filters for future anti-spam support</li>\n<li>Improvements to templates and CSS</li>\n<li>Improvements to Theme Compatibility</li>\n<li>Improved RTL support</li>\n<li>Improved multisite support</li>\n<li>Topic/reply trash fixes</li>\n</ul>\n<p><a href="http://wordpress.org/extend/plugins/bbpress/">Download bbPress 2.0 Beta 2</a></p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 May 2011 12:46:19 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"John James Jacoby";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:30;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:47:"Mark Jaquith: Developing on WordPress using Git";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"http://markjaquith.wordpress.com/?p=578";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:78:"http://markjaquith.wordpress.com/2011/05/26/developing-on-wordpress-using-git/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:6092:"<p>WordPress uses Subversion (SVN) for revision management. Before Subversion, it used CVS. Right now, Git is a hot option in the SCM category. It offers really nice features such as decentralization, speed, fast and cheap local branching, better merging, more offline capabilities, staging of commits, and lots more. It&#8217;s premature to talk about moving WordPress core and plugins to another SCM system — we have a lot invested with Subversion and Trac. But be of good cheer. You can have your Git and commit to Subversion too! Here&#8217;s how I do it.</p>\n<p>First, tools. You&#8217;ll need Git, obviously. But you&#8217;ll also need git-svn-diff, a Bash script that generates Subversion-compatible diffs.</p>\n<ul>\n<li><a href="http://git-scm.com/">Git</a></li>\n<li><a href="https://gist.github.com/833214">git-svn-diff</a></li>\n</ul>\n<p>Download git-svn-diff, put it somewhere in your path, and make it executable. Like this:</p>\n<pre class="brush: plain; light: true;">\ncurl -L http://rkj.me/a1 &gt; /usr/local/bin/git-svn-diff\nsudo chmod +x /usr/local/bin/git-svn-diff\n</pre>\n<p>Next, to enable you to do <code>git svn-diff</code> instead of <code>git-svn-diff</code>, edit <code>~/.gitconfig</code> and add this:</p>\n<pre class="brush: plain; light: true;">\n[alias]\n	svn-diff = !git-svn-diff\n</pre>\n<p>This next step is going to take a while. You&#8217;re going to pull down WordPress&#8217; SVN history using Git&#8217;s SVN support.</p>\n<pre class="brush: plain; light: true;">\ngit svn clone -t tags -b branches -T trunk http://core.svn.wordpress.org/\n</pre>\n<p>You might want to let that run overnight. Really. It&#8217;s going to go through each changeset.</p>\n<p>Once you&#8217;re done, you should be in the Git master branch, which corresponds to WordPress SVN&#8217;s trunk. WordPress&#8217; branches are in <code>remotes/{name}</code></p>\n<p>To pull in the latest changes from SVN, use <code>git svn rebase</code>. Important rule: never modify the SVN branches (<code>remotes/{name}</code>). Instead, create a new topic branch.</p>\n<p>For example, say that I&#8217;m going to work on a ticket for trunk. I&#8217;d create a new branch from <code>remotes/trunk</code> like this:</p>\n<pre class="brush: plain; light: true;">\ngit checkout -b ticket-12345 remotes/trunk\n</pre>\n<p>That will create a new local Git branch called <code>ticket-12345</code> based on SVN&#8217;s trunk, and then check it out (i.e. switch to it).</p>\n<p>If you&#8217;re working on a WordPress SVN branch, you can do something like this:</p>\n<pre class="brush: plain; light: true;">\ngit checkout -b ticket-12345 remotes/3.1\n</pre>\n<p>Do your work in the branch you created. You can make multiple local Git commits if you want, to break up your work into smaller chunks that make sense to you.</p>\n<p>When you&#8217;re ready to submit your patch, use git-svn-diff to produce it.</p>\n<pre class="brush: plain; light: true;">\ngit svn-diff &gt; ~/12345.diff\n</pre>\n<p>If you have commit access, you can commit to Subversion from this topic branch. But be careful! First you should do <code>git svn rebase</code> to bring your patch up to date. Next, you should squash your local git commits, otherwise each one of them will be individually committed to SVN (hello, flood). So rebase your commits into one commit, like so:</p>\n<pre class="brush: plain; light: true;">\ngit rebase -i remotes/trunk\n</pre>\n<p>Use &#8220;reword&#8221; on the first commit. Use &#8220;fixup&#8221; on the subsequent ones. That will roll the commits up into one. You&#8217;ll then be prompted to enter your amended commit message for that commit amalgam.</p>\n<p>Ready? You can now commit to SVN using:</p>\n<pre class="brush: plain; light: true;">\ngit svn dcommit\n</pre>\n<p>Git knows which remote SVN branch it came from when you checked out your topic branch. You can verify which one it is attached to by doing:</p>\n<pre class="brush: plain; light: true;">\ngit svn info\n</pre>\n<p>A few tips:</p>\n<p>Create a <code>.gitignore</code> file. This lists files or directories that you want Git to ignore. First, you want Git to ignore the <code>.gitignore</code> file itself! Next, you want Git to ignore your local <code>wp-config.php</code> Finally, you want to ignore any additional plugins, must-use plugins, themes, uploads, etc. Just do a <code>git status</code> and add anything that you don&#8217;t want to commit to WordPress or put in your patches.</p>\n<p>I hope you found this helpful! Let me know if you have any questions.</p>\n<br />  <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gocomments/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/comments/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godelicious/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/delicious/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gofacebook/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/facebook/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gotwitter/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/twitter/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/gostumble/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/stumble/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/godigg/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/digg/markjaquith.wordpress.com/578/" /></a> <a rel="nofollow" href="http://feeds.wordpress.com/1.0/goreddit/markjaquith.wordpress.com/578/"><img alt="" border="0" src="http://feeds.wordpress.com/1.0/reddit/markjaquith.wordpress.com/578/" /></a> <img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=markjaquith.wordpress.com&blog=316&post=578&subd=markjaquith&ref=&feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Fri, 27 May 2011 03:14:49 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Mark Jaquith";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:31;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:31:"Matt: ZURB Talk in Campbell, CA";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38278";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:46:"http://ma.tt/2011/05/zurb-talk-in-campbell-ca/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:245:"<p>Tomorrow I&#8217;m going to be speaking at <a href="http://www.zurb.com/article/674/rsvp-for-zurbsoapbox-w-wordpress-founder-">ZURBsoapbox in Campbell, California at noon</a>. If you&#8217;re in the Bay Area please come out and say howdy.</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 26 May 2011 15:30:03 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:32;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:64:"Weblog Tools Collection: WordPress 3.1.3 and 3.2 Beta 2 Released";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10063";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/-KM8wpYZ4wQ/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:934:"<p><a href="http://wordpress.org/">WordPress</a> 3.1.3 and 3.2 Beta 2 <a href="http://wordpress.org/news/2011/05/wordpress-3-1-3/">have been released</a>. Both releases include <a href="http://core.trac.wordpress.org/log/branches/3.1/?action=stop_on_copy&mode=stop_on_copy&rev=18023&stop_rev=17805&limit=100">a number of security fixes</a> and are recommended for all users.</p>\n<p>WordPress 3.2 Beta 2 also introduces support for <a href="http://code.google.com/chrome/chromeframe/">Google Chrome Frame</a>, an enhanced blue Dashboard color scheme, and <a href="http://wpdevel.wordpress.com/2011/05/25/jquery-updates-in-wordpress-3-2/">a new version of jQuery</a>.</p>\n<p>Don&#8217;t delay, upgrade today!™ And, if you run into problems, contact the <a href="http://wordpress.org/support/">WordPress Support Forums</a>.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/-KM8wpYZ4wQ" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Thu, 26 May 2011 13:00:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:33;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:45:"Matt: I Don’t Care if You Read This Article";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38273";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:67:"http://ma.tt/2011/05/i-don%e2%80%99t-care-if-you-read-this-article/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:813:"<div class="blockquote"><blockquote><p>The Internet measures everything. And I am a slave to those measurements. After so many years of pushing much of my life through this screen, I’ve started measuring my experiences and my sense of self-worth using the same metrics as the Internet uses to measure success. I check my stats relentlessly. The sad truth is that I spend more time measuring than I spend doing.</p></blockquote></div>\n<p>Fantastic read over at <a href="http://tweetagewasteland.com/2011/05/i-dont-care-if-you-read-this-article/">Tweetage Wasteland : I Don’t Care if You Read This Article</a>. Or put another way &#8220;Not everything that can be counted counts, and not everything that counts can be counted.&#8221; <cite>Hat tip: <a href="http://romanticrobot.net/">Mark Riley</a>.</cite></p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 25 May 2011 21:15:37 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:34;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:52:"Dev Blog: WordPress 3.1.3 (and WordPress 3.2 Beta 2)";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:33:"http://wordpress.org/news/?p=1838";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:50:"http://wordpress.org/news/2011/05/wordpress-3-1-3/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:3480:"<p><a href="http://wordpress.org/download/">WordPress 3.1.3</a> is available now and is a security update for all previous versions. It contains the following security fixes and enhancements:</p>\n<ul>\n<li>Various security hardening by <a href="http://www.buayacorp.com">Alexander Concha</a>.</li>\n<li>Taxonomy query hardening by <a href="http://johnlamansky.com/wordpress">John Lamansky</a>.</li>\n<li>Prevent sniffing out user names of non-authors by using canonical redirects. Props <a href="http://www.talsoft.com.ar">Verónica Valeros</a>.</li>\n<li>Media security fixes by Richard Lundeen of <a href="http://www.microsoft.com/">Microsoft</a>, Jesse Ou of <a href="http://www.microsoft.com/">Microsoft</a>, and <a href="http://www.microsoft.com/security/msrc/default.aspx">Microsoft Vulnerability Research</a>.</li>\n<li>Improves file upload security on hosts with dangerous security settings.</li>\n<li>Cleans up old WordPress import files if the import does not finish.</li>\n<li>Introduce &#8220;clickjacking&#8221; protection in modern browsers on admin and login pages.</li>\n</ul>\n<p>Consult the <a href="http://core.trac.wordpress.org/log/branches/3.1/?action=stop_on_copy&mode=stop_on_copy&rev=18023&stop_rev=17805&limit=100">change log</a> for more details.</p>\n<p><a href="http://wordpress.org/download/">Download WordPress 3.1.3</a> or update automatically from the Dashboard → Updates menu in your site’s admin area.</p>\n<hr />\n<h3>WordPress 3.2 Beta 2 also available</h3>\n<p>In other news, our development of WordPress 3.2 development continues right on schedule. We released <a href="http://wordpress.org/news/2011/05/wordpress-3-2-beta-1/">Beta 1</a> thirteen days ago, and today we&#8217;re putting out Beta 2 for your testing pleasure.</p>\n<p>This is still beta software, so <strong>we don&#8217;t recommend that you use it on production sites</strong>. But if you&#8217;re a plugin developer, a theme developer, or a site administrator, <strong>you should be running this on your test environments</strong> and <a href="http://codex.wordpress.org/Reporting_Bugs">reporting any bugs</a> you find. If you&#8217;re a WordPress user who wants to open your presents early, take advantage of WordPress&#8217; famous 5-minute install and spin up a secondary test site. Let us know what you think!</p>\n<p>The plan is to start putting out release candidates in early June, and to release WordPress 3.2 by the end of the month. The more you help us iron out issues during the beta period, the more likely we are to hit those dates. To misappropriate and mangle a quote from Mahatma Gandhi: &#8220;Be the punctuality you want to see in the WordPress.&#8221; In other words, test now!</p>\n<p>Here are some of the things that changed since Beta 1:</p>\n<ul>\n<li><a href="http://code.google.com/chrome/chromeframe/">Google Chrome Frame</a> is now supported in the admin, if you have it installed. This is especially useful for IE 6 users (remember, IE 6 is otherwise deprecated for the admin).</li>\n<li>The admin is less ugly in IE 7.</li>\n<li>The blue admin color scheme has caught up to the grey one, and is ready for testing.</li>\n<li>We are now bundling jQuery 1.6.1. You should test any JS that uses jQuery. WordPress JavaScript guru Andrew Ozz has <a href="http://wpdevel.wordpress.com/2011/05/25/jquery-updates-in-wordpress-3-2/">a post with more info</a>.</li>\n</ul>\n<p><a href="http://wordpress.org/wordpress-3.2-beta2.zip">Download WordPress 3.2 Beta 2</a></p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 25 May 2011 18:43:28 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Mark Jaquith";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:35;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:58:"Weblog Tools Collection: WordPress Theme Releases for 5/25";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:41:"http://weblogtoolscollection.com/?p=10056";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:73:"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/x3qhc0iV0yI/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1204:"<p><img class="alignnone size-full wp-image-10057" title="flow" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/flow.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://emptynestthemes.emptynestheritage.com/?p=1325"><strong>Flow</strong></a> is a natural, clean, and refreshing theme</p>\n<p><img class="alignnone size-full wp-image-10058" title="poweredby" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/poweredby.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://themeshaper.com/2011/05/24/powering-your-design-with-wordpress/"><strong>Powered By</strong></a> is a beautifully designed theme, packed with features, and built especially to tear apart and learn from the code.</p>\n<p><img class="alignnone size-full wp-image-10059" title="zeecorporate" src="http://weblogtoolscollection.com/wp-content/uploads/2011/05/zeecorporate.jpg" alt="" width="150" height="113" /></p>\n<p><a href="http://themezee.com/zeeCorporate/"><strong>zeeCorporate</strong></a> is an elegant and light WordPress theme for corporate blogs or business sites.</p>\n<img src="http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/x3qhc0iV0yI" height="1" width="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 25 May 2011 13:00:53 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"James Huff";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:36;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:79:"Lorelle on WP: Managing Multiple Authors: Customizing the WordPress Author Page";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:36:"http://lorelle.wordpress.com/?p=5079";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:104:"http://lorelle.wordpress.com/2011/05/24/managing-multiple-authors-customizing-the-wordpress-author-page/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:491:"We&#8217;ve been covering a lot of different suggestions for increasing the exposure of a contributor in a multiple author blog, and in this article, I want to focus specifically on one of the most important page on a WordPress site to an author, the author page. The author page in WordPress is generated automatically, featuring [...]<img alt="" border="0" src="http://stats.wordpress.com/b.gif?host=lorelle.wordpress.com&blog=72&post=5079&subd=lorelle&ref=&feed=1" width="1" height="1" />";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 25 May 2011 03:21:00 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:17:"Lorelle VanFossen";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:37;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:21:"Matt: Opulence Bubble";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:21:"http://ma.tt/?p=38268";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:37:"http://ma.tt/2011/05/opulence-bubble/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:843:"<div class="blockquote"><blockquote><p>I believe the mini-bubbles above are different ripples in what might call the surface of a superbubble: an opulence bubble. Here&#8217;s what I mean by opulence bubble: our conception of the good life, as I&#8217;ve discussed with you, has been centered on what I call hedonic opulence — having more, bigger, faster, cheaper, now. But we might be finding out, the hard way, that the pursuit of lowest-common-denominator industrial age stuff might have been steeply overvalued, in terms of its social, human, and financial value.</p></blockquote></div>\n<p><a href="http://blogs.hbr.org/haque/2011/05/the_opulence_bubble.html">The Opulence Bubble by Umair Haque</a>. <cite>Hat tip: <a href="http://www.tbray.org/ongoing/">Tim Bray</a> and <a href="http://paul.kedrosky.com/">Paul Kedrosky</a>.</cite></p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 24 May 2011 15:41:53 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"Matt";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:38;a:6:{s:4:"data";s:13:"\n	\n	\n	\n	\n	\n	\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:81:"Lorelle on WP: Managing Multiple Bloggers: Author Content Management on WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:36:"http://lorelle.wordpress.com/?p=5039"